import React, {useState, useEffect} from 'react';
import { Button, Nav, Container, Navbar, Row, Col, Image } from 'react-bootstrap';
import './App.scss';
import { Link, Route, Switch } from 'react-router-dom';
import axios from 'axios';
import { useMediaQuery } from 'react-responsive';

import Home from './Home.js';
import Detail from './Detail.js';
import Post from './Post.js';

function App() {
	const isPc = useMediaQuery({
		query: "(min-width:1200px)"
	});
	const isTablet = useMediaQuery({
		query : "(min-width:768px) and (max-width:1200px)"
	});
	const isMobile = useMediaQuery({
		query : "(max-width:767px)"
	});


	return (
		<div className="App">
			<Container fluid className="container-navbar">
				<Navbar expand="md" variant="dark">
					<Navbar.Brand as={Link} to="/"> Home </Navbar.Brand>
					<Navbar.Toggle aria-controls="basic-navbar-nav" />
					<Navbar.Collapse id="basic-navbar-nav">
						<Nav className="ml-auto">
							<Nav.Link as={Link} to="/detail">Detail</Nav.Link>
							<Nav.Link as={Link} to="/post">Post</Nav.Link>
						</Nav>
					</Navbar.Collapse>
				</Navbar>
			</Container>

			<Row className="container-main">
				{ isPc && 
					<Col xl="2" className="container-profile">
						<div>
     						<Image src="/images/profile.png" roundedCircle className="image-profile mb-2" />
							<h4> 김동환 </h4>
							<p> ASD AAA</p>
						</div>
					</Col>
				}
				<Col className="container-content">	
					<Switch>
						<Route exact path="/" component={Home} />
						<Route path="/detail" component={Detail} />
						<Route path="/post" component={Post} />
					</Switch>
				</Col>
			</Row>





		</div>

	);
}




export default App;
