import React, {useState} from 'react';
import './App.css';

function App() {
	let [modal, setmodal] = useState(false);
	let [seletedNumber, setSeletedNumber] = useState(0);

	return (
		<div className="App">
			<TestPropsChildren>
				<button onClick={()=>{setmodal(data=>!data)}}> ON </button>
				<div>
					{/* <Test1 /> */}

					<button onClick={()=>{ setSeletedNumber(0) }}>버튼1</button>
					<button onClick={()=>{ setSeletedNumber(1) }}>버튼2</button>
					<button onClick={()=>{ setSeletedNumber(2) }}>버튼3</button>
				</div>


				{
					modal === true
					? <Modal num={seletedNumber}></Modal>
					: null
				}
			</TestPropsChildren>
		</div>
	);
}


function Modal(props){
	let {num} = props;

	return(
		<div className="modal">
			<h2>{num}</h2>
			<p>날짜</p>
			<p>상세내용</p>
		</div>
	);
}

function Test1(){
	let [test, setTest] = useState(0);
	let [a, setA] = useState([1,2]);

	function asd(i){
		let newArray = [...a];
		newArray[i] += 1;
		setA(newArray);
	}

	return(
		<div>
			<p><span onClick={()=>{setTest(data=>data+1)}}>Click : </span>  {test}</p>

			<p onClick={()=>{asd(0);}}>aaaaaaaa</p>
			<p onClick={()=>{asd(1);}}>bbbbbbbb</p>
			<p>{a[0]}</p>
			<p>{a[1]}</p>
		</div>
	);
}

function TestPropsChildren(props){ // wrapper 역할로 사용...
	return(
		<div className="wrapper">
			{props.children}
		</div>
	);
}

export default App;
