import React, {useState} from 'react';
import './App.css';

function App() {
	let [a, setA] = useState([1,2]);
	let [modal, setmodal] = useState(false);
	let [test, setTest] = useState(0);

	function asd(i){
		let newArray = [...a];
		newArray[i] += 1;
		setA(newArray);
	}


	return (
		<div className="App">
			<div>
				<p><span onClick={()=>{setTest(data=>data+1)}}>Click : </span>  {test}</p>

				<p onClick={()=>{asd(0);}}>aaaaaaaa</p>
				<p onClick={()=>{asd(1);}}>bbbbbbbb</p>
				<p>{a[0]}</p>
				<p>{a[1]}</p>

				<p onClick={()=>{setmodal(!modal)}}> ccccc</p>
			</div>
			{
				modal === true
				? <Modal> asdaa?? <div>asda</div></Modal>
				: null
			}
		</div>
	);
}

function Modal({children}){
	return(
		<div className="modal">
			<h2>제목</h2>
			<p>{children}</p>
			<p>상세내용</p>
		</div>
	)
}

export default App;
