/*eslint-disable*/

import React, {useState} from 'react';
import { Jumbotron, Button, Col, Row, Card, Container, Navbar, Nav } from 'react-bootstrap';
import './App.css';
import { BrowserRouter, Link, Route, Switch, useHistory } from 'react-router-dom';

import datas from './data.js';
import Detail from './detail.js';
import {} from './practice.js';


function App() {

	return (
		<div className="App">

			<TestRouter />

		</div>
	);
}

function TestRouter(){
	let [post, setPost] = useState(datas);


	function sortContent(){
		let newPost = [...post];

		newPost.sort((data1, data2)=>{
			let d1 = data1.title.toUpperCase();
			let d2 = data2.title.toUpperCase();

			if (d1 < d2)
				return -1;
			else
				return 1;
		})

		setPost(newPost);
	}

	return(
	<div>
		<Navbar bg="light" expand="lg">
			<Navbar.Brand href="#home">Nav-Bar</Navbar.Brand>
			<Navbar.Toggle aria-controls="basic-navbar-nav" />
			<Navbar.Collapse id="basic-navbar-nav">
				<Nav className="mr-auto">
					<Nav.Link><Link to="/"> home </Link></Nav.Link>
				</Nav>
			</Navbar.Collapse>
		</Navbar>

		<Switch>
			<Route path="/" exact>
				<Jumbotron className="siteJumbotron">
					<h1>Hello, world!</h1>
					<p>
					This is a simple hero unit, a simple jumbotron-style component for calling
					extra attention to featured content or information.
					</p>
					<p>
						<Button variant="primary">Learn more</Button>
					</p>
				</Jumbotron>
				<Button variant="primary" className="mb-4" onClick={sortContent}> Sort </Button>
				<CardContent post={post} />
			</Route>
			<Route path="/detail/:id">
				<Detail post={post}/>
			</Route>
		</Switch>

	</div>
	);
}

function CardContent(props){
	let {post} = props;
	let history = useHistory();

	return(
		<div>
			<Container>
				<Row>
					{
						post.map((data, i) => {
							return(
								<Col md={4}> 
									<Card><Card.Body>
										<Card.Title> {data.title} </Card.Title>
										<Card.Subtitle className="mb-2 text-muted">{data.price}</Card.Subtitle>
										<Card.Text>
											{data.content}
										</Card.Text>
										<Button onClick={()=>{history.push("/detail/" + i)}}> Detail </Button>
									</Card.Body></Card>
								</Col>
							)
						})
					}
				</Row>
			</Container>
		</div>
	);

}



export default App;
